        //DO NOT CHANGE THESE LINES FOR THE ACTIVITY
        //The following code is the wordle color system
        public static boolean ColorCodedGuess (char[] inputWord, char[] correctWord) {
            boolean correct = true;
            char[] currentAnswer = correctWord;
            int[] letterColor = new int[5];
            
            //DO NOT CHANGE THESE LINES FOR THE ACTIVITY
            //The following code checks for any letters that need to be marked as "green"
            for (int i = 0; i < 5; i++) {
                if (inputWord[i] == currentAnswer[i]) {
                    currentAnswer[i] = '-';
                    letterColor[i] = 2;
                } else correct = false;
            }
            
            //DO NOT CHANGE THESE LINES FOR THE ACTIVITY
            //The following code checks for any letters that need to be marked as "yellow"
            for (int j = 0; j < 5; j++) {
                for (int k = 0; k < 5; k++){
                    if (inputWord[j] == currentAnswer[k] && letterColor[j] != 2) {
                        //DO NOT CHANGE THESE LINES FOR THE ACTIVITY
                        //accounts for grey letters
                        letterColor[j] = 1;
                        currentAnswer [k] = '-';
                    }
                }
            }

            //DO NOT CHANGE THESE LINES FOR THE ACTIVITY
            //The following code is the color labeling key for letters
            for (int m = 0; m < 5; m++) {
                if (letterColor[m] == 0) System.out.print(inputWord[m]);
                if (letterColor[m] == 1) System.out.print(ANSI_YELLOW + inputWord[m] + ANSI_RESET);
                if (letterColor[m] == 2) System.out.print(ANSI_GREEN + inputWord[m] + ANSI_RESET);
            }
            System.out.println("\n\n----------\nType your next guess below:\n");
            return correct;
        }


